package com.k.blog.dao;import com.k.blog.model.vo.AttachVo;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.SelectProvider;import org.apache.ibatis.jdbc.SQL;import java.util.List;/** * @author kangkai on 2018/7/9. */@Mapperpublic interface AttachDao {    @SelectProvider(type = AttachDaoProvider.class, method = "getAttachList")    List<AttachVo> getAttachList();    @SelectProvider(type = AttachDaoProvider.class, method = "getCount")    Long getCount();    class AttachDaoProvider {        public String getAttachList() {            return new SQL(){{                SELECT("*");                FROM("t_attach");                ORDER_BY("created");            }}.toString();        }        public String getCount() {            return getAttachList().replace("*","count(*)");        }    }}