package com.k.blog.dao;import com.k.blog.model.vo.LogVo;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * @author kangkai on 2018/4/23. */@Mapperpublic interface LogDao {    @Insert("insert into t_logs (action,data,author_id,ip,created) values (#{action},#{data},#{authorId},#{ip},#{created})")    int addLog(LogVo logVo);    @Select("select * from t_logs  order by created desc limit #{pageNow} , #{limit}")    List<LogVo> getLogs(@Param("pageNow") int pageNow, @Param("limit") int limit);}