package com.k.blog.dao;import com.k.blog.model.vo.MetaVo;import org.apache.ibatis.annotations.SelectProvider;import org.apache.ibatis.jdbc.SQL;import java.util.List;/** * @author kangkai on 2018/7/27. */public interface MetaDao {    @SelectProvider(type = MetaDaoProvider.class, method = "getMetaList")    List<MetaVo> getMetaList();    @SelectProvider(type = MetaDaoProvider.class, method = "getCount")    Long getCount();    class MetaDaoProvider {        public String getMetaList() {            return new SQL(){{                SELECT("*");                FROM("t_metas");                ORDER_BY("created");            }}.toString();        }        public String getCount() {            return new SQL(){{                SELECT("count(*)");                FROM("t_metas");            }}.toString();        }    }}