package com.k.blog.dao;import com.k.blog.model.vo.UserVo;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import java.util.List;/** * @author kangkai on 2018/4/17. */@Mapperpublic interface UserDao {    @Select("SELECT count(*) FROM t_users WHERE username = #{username}")    int findByUsername(@Param("username") String username);    @Select("SELECT * FROM t_users WHERE username = #{username} and password = #{password}")    UserVo login(@Param("username") String username,                 @Param("password") String password);    @Select("SELECT count(*) FROM t_users t WHERE username = #{username} and password = #{password}")    int login01(@Param("username") String username,                 @Param("password") String password);    @Update("update t_users set screen_name = #{screenName} ,email = #{email} where uid = #{uid}")    int updateUser4profile(UserVo userVo);    @Update("update t_users set password = #{password} where uid = #{uid}")    int updateUser4password(UserVo userVo);    @Select("select t_users where uid = #{uid}")    UserVo queryUserById(@Param("uid") Integer uid);}