package com.k.blog.mapper;import com.k.blog.model.vo.UserVo;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * @author kangkai on 2018/4/17. */public interface UserMapper {    @Select("SELECT count(*) FROM t_users WHERE username = #{username}")    int findByUsername(@Param("username") String username);    @Select("SELECT * FROM t_users WHERE username = #{username} and password = #{password}")    UserVo login(@Param("username") String username,                 @Param("password") String password);    @Select("SELECT count(*) FROM t_users t WHERE username = #{username} and password = #{password}")    int login01(@Param("username") String username,                 @Param("password") String password);}