package com.k.blog.service;import com.k.blog.model.vo.UserVo;/** * @author kangkai on 2018/4/20. */public interface UserService {    /**    * Description:用户登录接口     * 登录失败 直接抛出异常     * 登录成功 返回user实体    * @param username    * @param password    */    UserVo login(String username, String password);    /**     * 更新user - 个人信息     * @param userVo     */    int updateUser4frofile(UserVo userVo);    /**     * 更新user - 密码     * @param userVo     */    int updateUser4password(UserVo userVo);    /**     * Description:用户修改密码 校验旧密码     * 校验结果：成功 返回1     * 校验结果：失败 返回0     * @param username     * @param username     */    void check4updatePwd(String username, String password);    /**     * Description:根据id 查到user     * @param userId     */    UserVo queryUserById(Integer userId);}