package com.k.blog.service.impl;import com.k.blog.dao.LogDao;import com.k.blog.model.vo.LogVo;import com.k.blog.service.LogService;import com.k.blog.utils.DateKit;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author kangkai on 2018/4/23. */@Servicepublic class LogServiceImpl implements LogService {    @Autowired    LogDao logDao;    @Override    public void insertLog(String action,String data,Integer authorId,String ip) {        int created = DateKit.getCurrentUnixTime();        LogVo log = new LogVo();        log.setAction(action);        log.setData(data);        log.setAuthorId(authorId);        log.setIp(ip);        log.setCreated(DateKit.getSecondByInstant());        logDao.addLog(log);    }    @Override    public List<LogVo> getLogs(int pageNow, int limit) {        return logDao.getLogs(pageNow,limit);    }}