package com.k.blog.service.impl;import com.k.blog.enums.UserAction;import com.k.blog.mapper.LogMapper;import com.k.blog.model.vo.LogVo;import com.k.blog.model.vo.UserVo;import com.k.blog.service.LogService;import com.k.blog.utils.DateKit;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;/** * @author kangkai on 2018/4/23. */@Servicepublic class LogServiceImpl implements LogService {    @Autowired    LogMapper logMapper;    @Override    public void insertLog(String action,String data,Integer authorId,String ip) {        int created = DateKit.getCurrentUnixTime();        LogVo log = new LogVo();        log.setAction(action);        log.setData(data);        log.setAuthorId(authorId);        log.setIp(ip);        log.setCreated(DateKit.getCurrentUnixTime());        logMapper.addLog(log);    }}