package com.k.blog.web;import com.k.blog.model.vo.UserVo;import com.k.blog.utils.MapCache;import com.k.blog.utils.TaleUtils;import javax.servlet.http.HttpServletRequest;/** * @author kangkai on 2018/4/19. */public class BaseController {    public static String THEME = "themes/default";    protected MapCache cache = MapCache.single();    /**     * 主页的页面主题     * @param viewName     * @return     */    public String render(String viewName) {        return THEME + "/" + viewName;    }    /**     * 绑定标题     */    public BaseController title(HttpServletRequest request, String title) {        request.setAttribute("title", title);        return this;    }    /**     * 绑定关键词     */    public BaseController keywords(HttpServletRequest request, String keywords) {        request.setAttribute("keywords", keywords);        return this;    }    /**     * 获取请求绑定的登录对象     * @param request     * @return     */    public UserVo getUserFromRequest(HttpServletRequest request) {        return TaleUtils.getLoginUser(request);    }}