package com.k.blog.web;import com.k.blog.entity.User;import com.k.blog.mapper.UserMapper;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;/** * @author kangkai on 2018/4/17. */@RestController@RequestMapping("/testUser")public class UserController {    @Autowired    private UserMapper userMapper;    private static final Logger logger = LoggerFactory.getLogger(UserController.class);    @RequestMapping("/getUserById")    @ResponseBody    public User getUserById(long id) {        User user = userMapper.findById(id);        logger.debug("success");        logger.info("success");        logger.trace("success");        logger.error("success");        logger.warn("success");        return user;    }}